CREATE DEFINER=`root`@`localhost` PROCEDURE `v_insumosAltaOP`(			
			_id_receta int,
            _peso_paston decimal(10,3))
BEGIN
	SELECT v_ir.*,v_si.stock ,round(((cantidad/100)*_peso_paston),3) as cantidad_op, round((v_si.stock-((cantidad/100)*_peso_paston)),3) as diferencia
	FROM v_insumosreceta v_ir
	inner join v_stockinsumos v_si on v_si.id_insumo=v_ir.id_insumo
	where id_receta=_id_receta 
	order by id_receta,insumo;
END ;;
