			_id_receta int,
            _peso_paston decimal(10,3))
BEGIN
SELECT v_ir.*,v_si.stock ,((cantidad/100)*_peso_paston) as cantidad_op, (v_si.stock-((cantidad/100)*_peso_paston)) as diferencia
FROM v_insumosreceta v_ir
inner join v_stockinsumos v_si on v_si.id_insumo=v_ir.id_insumo
where id_receta=_id_receta and (v_si.stock-((cantidad/100)*_peso_paston)) < 0 
order by id_receta,insumo;

END ;;
